const BASE_URL = "http://localhost:3000/api/todos"; // Update if backend runs on another port

export const extractTodos = async (text) => {
    const response = await fetch(`${BASE_URL}/extract-todos`, {
        method: "POST",
        headers: { "Content-Type": "application/json" },
        body: JSON.stringify({ para: text }),
    });
    return response.json();
};

export const getTodos = async () => {
    const response = await fetch(`${BASE_URL}/get-todos`);
    return response.json();
};

export const deleteTodo = async (id) => {
    await fetch(`${BASE_URL}/delete-todos/${id}`, { method: "DELETE" });
};
