import React from "react";
import { deleteTodo } from "../api";

function TodoList({ todos, setTodos }) {
    const handleDelete = async (id) => {
        await deleteTodo(id);
        setTodos(todos.filter(todo => todo._id !== id)); // Remove from UI
    };

    return (
        <div className="mt-4">
            <h2 className="text-center">üìù Your Todos</h2>
            <ul className="list-group">
                {todos.map((todo) => (
                    <li key={todo._id} className="list-group-item d-flex justify-content-between align-items-center">
                        <div>
                            <strong>{todo.task}</strong> <br />
                            <small className="text-muted">Deadline: {todo.deadline} {todo.time}</small>
                        </div>
                        <button className="btn btn-danger btn-sm" onClick={() => handleDelete(todo._id)}>üóë Delete</button>
                    </li>
                ))}
            </ul>
        </div>
    );
}

export default TodoList;
