import React from "react";

const TodoList = ({ todos, onDelete }) => {
    return (
        <div className="bg-white shadow-md rounded-lg p-4 mt-6 w-full max-w-md">
            <h2 className="text-xl font-semibold mb-4">üìù Extracted Todos</h2>
            {todos.length === 0 ? (
                <p className="text-gray-500">No todos found</p>
            ) : (
                <ul>
                    {todos.map((todo) => (
                        <li key={todo._id} className="flex justify-between items-center border-b py-2">
                            <div>
                                <strong>{todo.task}</strong> - <span>{todo.status}</span> 
                                <br />
                                <span className="text-gray-500 text-sm">Deadline: {todo.deadline || "N/A"}</span>
                            </div>
                            <button 
                                onClick={() => onDelete(todo._id)}
                                className="bg-red-500 text-white px-2 py-1 rounded hover:bg-red-700"
                            >
                                ‚ùå Delete
                            </button>
                        </li>
                    ))}
                </ul>
            )}
        </div>
    );
};

export default TodoList;
